/**
 * Excel equivalent:
 * =IF(ISBLANK(BY),
 *     IFERROR(VLOOKUP(Q, PaymentTerms, "Available Payment Terms"), ""),
 *     "Cancelled")
 *
 * @param cancelMarker   (BY column) if non-blank => "Cancelled"
 * @param paymentCondition (Q column) one of "Invalid Booking", "Last Minute Booking", "Standard Booking, P1-4"
 * @returns string: Available Payment Term, "" if not found, or "Cancelled" if cancelled
 */

const paymentTerms = [
  { condition: "Invalid Booking", available: "Invalid" },
  { condition: "Last Minute Booking", available: "Full payment required within 48hrs" },
  { condition: "Standard Booking, P1", available: "P1" },
  { condition: "Standard Booking, P2", available: "P2" },
  { condition: "Standard Booking, P3", available: "P3" },
  { condition: "Standard Booking, P4", available: "P4" },
];

export default function availablePaymentTerm(
  cancelMarker: unknown,
  paymentCondition: string | null | undefined
): string {
  const isBlankLike = (v: unknown) =>
    v === null || v === undefined || (typeof v === "string" && v.trim() === "");

  // If BY is not blank → "Cancelled"
  if (!isBlankLike(cancelMarker)) return "Cancelled";

  // If payment condition is blank
  if (!paymentCondition || paymentCondition.trim() === "") return "";

  const target = paymentCondition.trim().toLowerCase();

  const found = paymentTerms.find(
    t => t.condition.trim().toLowerCase() === target
  );

  return found ? found.available : "";
}

/**
 * Replicates the Excel formula:
 * =IF(L="Single Booking","SB",IF(L="Duo Booking","DB",IF(L="Group Booking","GB","")))
 *
 * Logic:
 *  - If bookingType = "Single Booking" -> "SB"
 *  - If bookingType = "Duo Booking"    -> "DB"
 *  - If bookingType = "Group Booking"  -> "GB"
 *  - Else -> ""
 */
export default function bookingCodeColumn(bookingType: string | null | undefined): string {
  if (!bookingType) return "";

  if (bookingType === "Single Booking") return "SB";
  if (bookingType === "Duo Booking") return "DB";
  if (bookingType === "Group Booking") return "GB";

  return "";
}

/**
 * Replicates the Excel formula:
 * =IF(N="","",IF(COUNTA(L:T)<6,"", B & "-" & C & "-" & D & "-" & E & F))
 *
 * Logic:
 *  - If tourDate is blank -> return ""
 *  - If supportingFields count < 6 -> return ""
 *  - Else -> bookingId-customerId-packageId-inquiryIdsequenceNumber
 */
export default function generateBookingReference(
  tourDate: string | null | undefined,
  supportingFields: (string | null | undefined)[],
  bookingCode: string,
  tourCode: string,
  formattedDate: string,
  travelsInitial: string,
  tourPackageUniqueCounter: string
): string {
  // If tour date is blank
  if (!tourDate || tourDate.trim() === "") return "";

  // Count how many supporting fields are non-empty
  const count = supportingFields.filter(
    v => v !== null && v !== undefined && v.toString().trim() !== ""
  ).length;
  if (count < 6) return "";

  // Concatenate values with hyphens (note: inquiryId and sequenceNumber have no hyphen between them)
  return `${bookingCode}-${tourCode}-${formattedDate}-${travelsInitial}${tourPackageUniqueCounter}`;
}

/**
 * Excel equivalent:
 * =IF(AND(K<>"", N<>""), N - K, "")
 *
 * - K = Reservation Date
 * - N = Tour Date
 * - Returns: number of days between (N - K), or "" if blank/invalid
 */
export default function daysBetweenReservationAndTour(
  reservationDate: unknown,
  tourDate: unknown
): number | "" {
  const toDate = (input: unknown): Date | null => {
    if (input === null || input === undefined) return null;
    if (typeof input === "string" && input.trim() === "") return null;

    try {
      if (
        typeof input === "object" &&
        input !== null &&
        "toDate" in (input as any) &&
        typeof (input as any).toDate === "function"
      ) {
        return (input as any).toDate();
      }
      if (
        typeof input === "object" &&
        input !== null &&
        "seconds" in (input as any) &&
        typeof (input as any).seconds === "number"
      ) {
        const s = (input as any).seconds as number;
        const ns =
          typeof (input as any).nanoseconds === "number"
            ? (input as any).nanoseconds
            : 0;
        return new Date(s * 1000 + Math.floor(ns / 1e6));
      }
      if (input instanceof Date) return input;
      if (typeof input === "number") return new Date(input);
      if (typeof input === "string") {
        const raw = input.trim();
        if (/^\d{2}\/\d{2}\/\d{4}$/.test(raw)) {
          const [dd, mm, yyyy] = raw.split("/").map(Number);
          return new Date(yyyy, mm - 1, dd);
        }
        if (/^\d{4}-\d{2}-\d{2}$/.test(raw)) {
          const [yyyy, mm, dd] = raw.split("-").map(Number);
          return new Date(yyyy, mm - 1, dd);
        }
        return new Date(raw);
      }
      return null;
    } catch {
      return null;
    }
  };

  const res = toDate(reservationDate);
  const tour = toDate(tourDate);

  if (!res || isNaN(res.getTime()) || !tour || isNaN(tour.getTime())) return "";

  const msPerDay = 1000 * 60 * 60 * 24;
  const diff = Math.round((tour.getTime() - res.getTime()) / msPerDay);

  return diff;
}

/**
 * Excel equivalent:
 * =IF(OR(ISBLANK(K), ISBLANK(N)), "",
 *   LET(
 *     resDate, K,
 *     tourDate, N,
 *     fullPaymentDue, tourDate - 30,
 *     monthCount, MAX(0, DATEDIF(resDate, fullPaymentDue, "M") + 1),
 *     secondDates, DATE(YEAR(resDate), MONTH(resDate) + SEQUENCE(monthCount), 2),
 *     validDates, FILTER(secondDates, (secondDates >= resDate + 3) * (secondDates <= fullPaymentDue)),
 *     IF(ISERROR(validDates), 0, COUNTA(validDates))
 *   )
 * )
 *
 * Returns: number | ""  (empty string if either date is blank/invalid)
 */
export default function eligibleSecondsCount(
  reservationDate: unknown, // K
  tourDate: unknown         // N
): number | "" {
  // ---------- local helpers (robust parsing like our previous funcs) ----------
  const toDate = (input: unknown): Date | null => {
    if (input === null || input === undefined) return null;
    if (typeof input === "string" && input.trim() === "") return null;

    try {
      // Firestore Timestamp (has .toDate())
      if (
        typeof input === "object" &&
        input !== null &&
        "toDate" in (input as any) &&
        typeof (input as any).toDate === "function"
      ) {
        const d = (input as any).toDate();
        return isNaN(d.getTime()) ? null : d;
      }

      // Firestore-like { seconds, nanoseconds? }
      if (
        typeof input === "object" &&
        input !== null &&
        "seconds" in (input as any) &&
        typeof (input as any).seconds === "number"
      ) {
        const s = (input as any).seconds as number;
        const ns =
          typeof (input as any).nanoseconds === "number"
            ? (input as any).nanoseconds
            : 0;
        const d = new Date(s * 1000 + Math.floor(ns / 1e6));
        return isNaN(d.getTime()) ? null : d;
      }

      // Already a Date
      if (input instanceof Date) return isNaN(input.getTime()) ? null : input;

      // Milliseconds timestamp
      if (typeof input === "number") {
        const d = new Date(input);
        return isNaN(d.getTime()) ? null : d;
      }

      // String formats
      if (typeof input === "string") {
        const raw = input.trim();

        // dd/mm/yyyy
        if (/^\d{2}\/\d{2}\/\d{4}$/.test(raw)) {
          const [dd, mm, yyyy] = raw.split("/").map(Number);
          const d = new Date(yyyy, mm - 1, dd);
          return isNaN(d.getTime()) ? null : d;
        }

        // yyyy-mm-dd
        if (/^\d{4}-\d{2}-\d{2}$/.test(raw)) {
          const [yyyy, mm, dd] = raw.split("-").map(Number);
          const d = new Date(yyyy, mm - 1, dd);
          return isNaN(d.getTime()) ? null : d;
        }

        // Natural/ISO strings (incl. "UTC+8")
        const d = new Date(raw);
        return isNaN(d.getTime()) ? null : d;
      }

      return null;
    } catch {
      return null;
    }
  };

  const startOfDay = (d: Date) => new Date(d.getFullYear(), d.getMonth(), d.getDate());

  const addDays = (base: Date, days: number): Date => {
    const out = new Date(base);
    out.setDate(out.getDate() + days);
    return out;
  };

  // Similar behavior to DATEDIF(res, end, "M")
  const monthsBetweenInclusiveStart = (a: Date, b: Date): number => {
    const years = b.getFullYear() - a.getFullYear();
    const months = b.getMonth() - a.getMonth();
    let diff = years * 12 + months;
    if (b.getDate() < a.getDate()) diff -= 1;
    return Math.max(0, diff);
  };

  const generateMonthSeconds = (start: Date, count: number): Date[] => {
    const list: Date[] = [];
    for (let i = 0; i < count; i++) {
      list.push(new Date(start.getFullYear(), start.getMonth() + i, 2));
    }
    return list;
  };

  // ---------- apply logic ----------
  const res = toDate(reservationDate);
  const tour = toDate(tourDate);
  if (!res || !tour) return "";

  const resD = startOfDay(res);
  const tourD = startOfDay(tour);

  const fullPaymentDue = addDays(tourD, -30);           // tourDate - 30
  const windowStart = addDays(resD, 3);                 // resDate + 3

  // monthCount = MAX(0, DATEDIF(resDate, fullPaymentDue, "M") + 1)
  const monthCount = Math.max(0, monthsBetweenInclusiveStart(resD, fullPaymentDue) + 1);

  // secondDates = DATE(YEAR(resDate), MONTH(resDate) + SEQUENCE(monthCount), 2)
  const seconds = generateMonthSeconds(resD, monthCount);

  // validDates: seconds within [res+3, fullPaymentDue]
  const eligible = seconds.filter(d => d >= windowStart && d <= fullPaymentDue);

  // IF(ISERROR(validDates), 0, COUNTA(validDates))
  // In JS, filter won't error; count is just eligible.length
  return eligible.length;
}

/**
 * Excel equivalent: =IF(N="","",TEXT(N,"yyyymmdd"))
 *
 * Accepts:
 *  - null/undefined/"" -> returns ""
 *  - Date
 *  - number (ms since epoch)
 *  - string ("September 15, 2025 at 8:00:00 AM UTC+8", "15/09/2025", "2025-09-15")
 *  - Firestore Timestamp (has .toDate())
 *  - { seconds: number, nanoseconds?: number } shape
 *
 * Returns:
 *  - "yyyymmdd" for valid inputs
 *  - "ERROR" for invalid inputs
 */
export default function tourDateToYyyymmdd(tourDate: unknown): string {
    // 1) Blank handling
    if (tourDate === null || tourDate === undefined) return "";
    if (typeof tourDate === "string" && tourDate.trim() === "") return "";

    // 2) Try to normalize to a Date
    let date: Date | null = null;

    try {
        // Firestore Timestamp: has .toDate()
        if (typeof tourDate === "object" && tourDate !== null && "toDate" in (tourDate as any) && typeof (tourDate as any).toDate === "function") {
            date = (tourDate as any).toDate();
        }
        // Firestore-like { seconds, nanoseconds }
        else if (
            typeof tourDate === "object" &&
            tourDate !== null &&
            "seconds" in (tourDate as any) &&
            typeof (tourDate as any).seconds === "number"
        ) {
            const s = (tourDate as any).seconds as number;
            const ns = typeof (tourDate as any).nanoseconds === "number" ? (tourDate as any).nanoseconds : 0;
            date = new Date(s * 1000 + Math.floor(ns / 1e6));
        }
        // Already a Date
        else if (tourDate instanceof Date) {
            date = tourDate;
        }
        // Milliseconds timestamp
        else if (typeof tourDate === "number") {
            const d = new Date(tourDate);
            date = isNaN(d.getTime()) ? null : d;
        }
        // String inputs
        else if (typeof tourDate === "string") {
            const raw = tourDate.trim();

            // dd/mm/yyyy
            if (/^\d{2}\/\d{2}\/\d{4}$/.test(raw)) {
                const [dd, mm, yyyy] = raw.split("/").map(Number);
                date = new Date(yyyy, mm - 1, dd);
            }
            // yyyy-mm-dd
            else if (/^\d{4}-\d{2}-\d{2}$/.test(raw)) {
                const [yyyy, mm, dd] = raw.split("-").map(Number);
                date = new Date(yyyy, mm - 1, dd);
            }
            // Natural/ISO strings (includes "UTC+8")
            else {
                const parsed = new Date(raw);
                date = isNaN(parsed.getTime()) ? null : parsed;
            }
        }

        // 3) Validate
        if (!date || isNaN(date.getTime())) return "ERROR";

        // 4) Format yyyymmdd
        const y = date.getFullYear();
        const m = String(date.getMonth() + 1).padStart(2, "0");
        const d = String(date.getDate()).padStart(2, "0");
        return `${y}-${m}-${d}`;
    } catch {
        return "ERROR";
    }
}

