export default async function generateEmailDraft(
  bookingId: string,
  includeBcc: boolean | null,
  emailAddress: string,
  generateEmailDraft: boolean
): Promise<string> {
  try {
    // Get all bookings and filter client-side
    const bookings = await firebaseUtils.getCollectionData("bookings");

    if (!bookingId) return "";

    // Filter to find the booking with matching bookingId AND emailAddress
    const matchingBookings = bookings.filter(booking =>
      booking.bookingId === bookingId &&
      booking.emailAddress === emailAddress
    );

    if (!matchingBookings || matchingBookings.length === 0) {
      throw new Error(`Booking with bookingId ${bookingId} and email ${emailAddress} not found`);
    }

    // Get the first matching booking document
    const bookingDoc = matchingBookings[0];

    console.log(`Found booking document with ID: ${bookingDoc.id}`);

    // Check if booking already has an email draft
    const existingEmailDraftId = bookingDoc.emailDraftLink || bookingDoc.cancellationEmailDraftId;

    console.log("Email is existing: ", existingEmailDraftId)

    if (existingEmailDraftId) {
      if (generateEmailDraft) {
        // Return existing draft ID if Generate Email Draft is true
        console.log(`Returning existing email draft: ${existingEmailDraftId}`);
        return existingEmailDraftId;
      } else {
        // Delete existing draft if Generate Email Draft is false
        console.log(`Deleting existing email draft: ${existingEmailDraftId}`);
        await firebaseUtils.deleteDocument("emailDrafts", existingEmailDraftId);

        // Update booking to remove draft reference using document ID
        await firebaseUtils.updateDocument("bookings", bookingDoc.id, {
          emailDraftId: null,
          cancellationEmailDraftId: null,
          generateEmailDraft: false
        });

        return "";
      }
    }

    // If no existing draft and Generate Email Draft is true, create new draft
    if (generateEmailDraft) {
      console.log(`Generating new email draft for booking: ${bookingId}`);

      // Use the REAL Firebase Functions client instead of the mock
      const generateEmail = httpsCallable(functions, 'generateReservationEmail');

      try {
        const emailResult = await generateEmail({
          bookingId: bookingDoc.id, // Use the document ID, not the bookingId field
          generateDraftCell: true
        });

        console.log('Real function result:', emailResult.data);

        if (emailResult.data && emailResult.data.success) {
          const draftId = emailResult.data.draftId || "";
          console.log(`Generated email draft with ID: ${draftId}`);
          return draftId;
        } else {
          console.error('Function returned unsuccessful result:', emailResult.data);
          throw new Error("Failed to generate reservation email draft");
        }
      } catch (functionError) {
        console.error('Error calling generateReservationEmail function:', functionError);

        // Log detailed error information
        if (functionError.code) {
          console.error('Error code:', functionError.code);
        }
        if (functionError.message) {
          console.error('Error message:', functionError.message);
        }
        if (functionError.details) {
          console.error('Error details:', functionError.details);
        }

        // Re-throw with more context
        throw new Error(`Cloud function error: ${functionError.code || 'unknown'} - ${functionError.message || 'Unknown error'}`);
      }
    }

    // If Generate Email Draft is false and no existing draft, return empty string
    console.log(`No action needed - Generate Email Draft is false and no existing draft`);
    return "";

  } catch (error) {
    console.error("Error in generateEmailDraft:", error);
    throw error;
  }
}

/**
 * Fetch the subject from an email draft by its ID.
 *
 * @param draftLinkId Document ID of the draft in emailDrafts collection
 * @returns Subject string if found, "" if missing, "ERROR" if not found
 */
export default async function getEmailDraftSubject(
  draftLinkId: string
): Promise<string> {
  if (!draftLinkId || draftLinkId.trim() === "") return "";

  try {
    const drafts = await firebaseUtils.getCollectionData("emailDrafts");

    const draft = drafts.find((d: any) => d.id === draftLinkId);

    if (!draft) return "ERROR";
    return draft.subject ?? "";
  } catch (err) {
    console.error("Failed to fetch email draft:", err);
    return "ERROR";
  }
}

/**
 * Generate Group/Duo Booking Member ID (standalone version, no allRows needed).
 *
 * @param bookingType  "Duo Booking" | "Group Booking"
 * @param tourName     Tour package name
 * @param firstName    Traveller's first name
 * @param lastName     Traveller's last name
 * @param email        Traveller's email
 * @param isActive     Equivalent of U column (if false => "")
 * @returns string ID or ""
 */
export default function generateGroupMemberId(
  bookingType: string,
  tourName: string,
  firstName: string,
  lastName: string,
  email: string,
  isActive: boolean
): string {
  // Only Duo or Group bookings apply
  if (!(bookingType === "Duo Booking" || bookingType === "Group Booking")) {
    return "";
  }
  if (isActive == false) return "";

  const initials =
    (firstName?.[0] ?? "").toUpperCase() + (lastName?.[0] ?? "").toUpperCase();
  const idPrefix = bookingType === "Duo Booking" ? "DB" : "GB";

  // Hash based on email + traveller identity
  const identity = `${bookingType}|${tourName}|${firstName}|${lastName}|${email}`;
  let hashNum = 0;
  for (let i = 0; i < identity.length; i++) {
    hashNum += identity.charCodeAt(i) * (i + 1);
  }
  const hashTag = String(Math.abs(hashNum) % 10000).padStart(4, "0");

  // Fake member number: derive from hash as a stable 001–999
  const memberNumber = String((Math.abs(hashNum) % 999) + 1).padStart(3, "0");

  return `${idPrefix}-${initials}-${hashTag}-${memberNumber}`;
}

/**
 * Excel equivalent:
 * =IF(ISBLANK(N),"",
 *   IFS(
 *     AND(R=0, S<2), "Invalid Booking",
 *     AND(R=0, S>=2), "Last Minute Booking",
 *     R=1, "Standard Booking, P1",
 *     R=2, "Standard Booking, P2",
 *     R=3, "Standard Booking, P3",
 *     R>=4, "Standard Booking, P4"
 *   )
 * )
 *
 * @param tourDate                  The tour date (N column)
 * @param eligibleSecondCount       The number of eligible "2nd of month" dates (R column)
 * @param daysBetweenReservationAndTour  Days between reservation date and tour date (S column)
 * @returns "" | the payment condition string
 */
export default function paymentCondition(
  tourDate: unknown,
  eligibleSecondCount: number,
  daysBetweenReservationAndTour: number
): string {
  const isBlankLike = (v: unknown) =>
    v === null || v === undefined || (typeof v === "string" && v.trim() === "");

  if (isBlankLike(tourDate)) return "";

  if (
    typeof eligibleSecondCount !== "number" ||
    typeof daysBetweenReservationAndTour !== "number" ||
    !isFinite(eligibleSecondCount) ||
    !isFinite(daysBetweenReservationAndTour)
  ) {
    return ""; // fallback if inputs invalid
  }

  if (eligibleSecondCount === 0 && daysBetweenReservationAndTour < 2) {
    return "Invalid Booking";
  }
  if (eligibleSecondCount === 0 && daysBetweenReservationAndTour >= 2) {
    return "Last Minute Booking";
  }
  if (eligibleSecondCount === 1) return "Standard Booking, P1";
  if (eligibleSecondCount === 2) return "Standard Booking, P2";
  if (eligibleSecondCount === 3) return "Standard Booking, P3";
  if (eligibleSecondCount >= 4) return "Standard Booking, P4";

  return "";
}

/**
 * Excel equivalent:
 * =IF(N="","",IFERROR(N + VALUE(REGEXEXTRACT(P, "(\d+)\\s+Days")), ""))
 *
 * - N = tourDate (robust parsing like tourDateToYyyymmdd)
 * - P = durationDays (number of days from tourDurationByName)
 * - Returns: "yyyy-mm-dd" (end date) or "" if blank/error
 */
export default function tourEndDateFromStartAndDuration(
  tourDate: unknown,
  durationDays: number
): string {
  // --- helpers ---
  const toDate = (input: unknown): Date | null => {
    if (input === null || input === undefined) return null;
    if (typeof input === "string" && input.trim() === "") return null;

    try {
      if (
        typeof input === "object" &&
        input !== null &&
        "toDate" in (input as any) &&
        typeof (input as any).toDate === "function"
      ) {
        return (input as any).toDate();
      }
      if (
        typeof input === "object" &&
        input !== null &&
        "seconds" in (input as any) &&
        typeof (input as any).seconds === "number"
      ) {
        const s = (input as any).seconds as number;
        const ns =
          typeof (input as any).nanoseconds === "number"
            ? (input as any).nanoseconds
            : 0;
        return new Date(s * 1000 + Math.floor(ns / 1e6));
      }
      if (input instanceof Date) return input;
      if (typeof input === "number") return new Date(input);
      if (typeof input === "string") {
        const raw = input.trim();
        if (/^\d{2}\/\d{2}\/\d{4}$/.test(raw)) {
          const [dd, mm, yyyy] = raw.split("/").map(Number);
          return new Date(yyyy, mm - 1, dd);
        }
        if (/^\d{4}-\d{2}-\d{2}$/.test(raw)) {
          const [yyyy, mm, dd] = raw.split("-").map(Number);
          return new Date(yyyy, mm - 1, dd);
        }
        return new Date(raw); // ISO or natural string
      }
      return null;
    } catch {
      return null;
    }
  };

  const toStartOfDay = (d: Date): Date =>
    new Date(d.getFullYear(), d.getMonth(), d.getDate());

  const addDays = (base: Date, days: number): Date => {
    const out = new Date(base);
    out.setDate(out.getDate() + days);
    return out;
  };

  const formatYYYYMMDD = (d: Date): string => {
    const y = d.getFullYear();
    const m = String(d.getMonth() + 1).padStart(2, "0");
    const day = String(d.getDate()).padStart(2, "0");
    return `${y}-${m}-${day}`;
  };

  // --- logic ---
  const start = toDate(tourDate);
  if (!start || isNaN(start.getTime())) return "";
  if (typeof durationDays !== "number" || !Number.isFinite(durationDays)) return "";

  const end = addDays(toStartOfDay(start), durationDays);
  return formatYYYYMMDD(end);
}

/**
 * Safely send an email draft once (without runTransaction).
 *
 * @param draftLinkId Firestore doc ID in "emailDrafts"
 * @param sendEmail   Boolean flag, if false -> return ""
 * @returns "" if skipped, messageId if sent/already sent, "ERROR" if failure
 */
export default async function sendEmailDraftOnce(
  draftLinkId: string,
  sendEmail: boolean
): Promise<string> {
  if (!sendEmail) return "";
  if (!draftLinkId || draftLinkId.trim() === "") return "ERROR";

  try {
    // 1) Load draft
    const draft = await firebaseUtils.getDocumentData("emailDrafts", draftLinkId);
    if (!draft) return "ERROR";

    // If already sent, short-circuit
    if (draft.status === "sent") {
      return typeof draft.messageId === "string" ? draft.messageId : "";
    }

    // If currently sending by another process, bail out
    if (draft.status === "sending") {
      return "";
    }

    // 2) Mark as "sending" (soft lock)
    await firebaseUtils.updateDocument("emailDrafts", draftLinkId, {
      status: "sending",
      updatedAt: new Date(),
    });

    // 3) Call Cloud Function to actually send
    const callSend = httpsCallable(functions, "sendReservationEmail");
    const result: any = await callSend({ draftId: draftLinkId });

    if (result?.data?.success && typeof result.data.messageId === "string") {
      return ${result.data.messageId};
    }

    // Unexpected result → mark failed
    await firebaseUtils.updateDocument("emailDrafts", draftLinkId, {
