/**
 * Tour Package Unique Counter (by email, ordered by tour date)
 *
 * Excel-like intent:
 * - Scope to the same tourPackageName (M) and emailAddress (G)
 * - Count those whose tourDate is <= current row's tourDate
 * - Return as 3-digit string ("001", "002", ...)
 *
 * @param tourPackageName Current row's tour package name (M column)
 * @param emailAddress    Traveller's email (G column)
 * @param tourDate        Current row's tour date (can be string/Date/TS/Firestore Timestamp)
 * @returns "" if M/G blank, "ERROR" if invalid inputs, else "000"-formatted count
 */
export default async function tourPackageUniqueCounter(
  tourPackageName: string | null | undefined,
  emailAddress: string | null | undefined,
  tourDate: unknown
): Promise<string> {
  // --- blank checks (Excel-style) ---
  if (!tourPackageName || tourPackageName.trim() === "") return "";
  if (!emailAddress || emailAddress.trim() === "") return "";

  // --- normalize tourDate for comparison ---
  const toDate = (input: unknown): Date | null => {
    if (input === null || input === undefined) return null;
    if (typeof input === "string" && input.trim() === "") return null;

    try {
      // Firestore Timestamp
      if (
        typeof input === "object" &&
        input !== null &&
        "toDate" in (input as any) &&
        typeof (input as any).toDate === "function"
      ) {
        const d = (input as any).toDate();
        return isNaN(d.getTime()) ? null : d;
      }
      // Firestore-like { seconds, nanoseconds }
      if (
        typeof input === "object" &&
        input !== null &&
        "seconds" in (input as any) &&
        typeof (input as any).seconds === "number"
      ) {
        const s = (input as any).seconds as number;
        const ns =
          typeof (input as any).nanoseconds === "number"
            ? (input as any).nanoseconds
            : 0;
        const d = new Date(s * 1000 + Math.floor(ns / 1e6));
        return isNaN(d.getTime()) ? null : d;
      }
      if (input instanceof Date) return isNaN(input.getTime()) ? null : input;
      if (typeof input === "number") {
        const d = new Date(input);
        return isNaN(d.getTime()) ? null : d;
      }
      if (typeof input === "string") {
        const raw = input.trim();
        // dd/mm/yyyy
        if (/^\d{2}\/\d{2}\/\d{4}$/.test(raw)) {
          const [dd, mm, yyyy] = raw.split("/").map(Number);
          const d = new Date(yyyy, mm - 1, dd);
          return isNaN(d.getTime()) ? null : d;
        }
        // yyyy-mm-dd
        if (/^\d{4}-\d{2}-\d{2}$/.test(raw)) {
          const [yyyy, mm, dd] = raw.split("-").map(Number);
          const d = new Date(yyyy, mm - 1, dd);
          return isNaN(d.getTime()) ? null : d;
        }
        const d = new Date(raw); // ISO/natural, respects "UTC+8" if present
        return isNaN(d.getTime()) ? null : d;
      }
      return null;
    } catch {
      return null;
    }
  };

  const currentTourDate = toDate(tourDate);
  if (!currentTourDate) return "ERROR";

  // --- validate tour package exists in master (tourPackages) ---
  const tourPackages = await firebaseUtils.getCollectionData("tourPackages");
  const inMaster = tourPackages.some(
    (p: any) =>
      typeof p?.name === "string" &&
      p.name.trim().toLowerCase() === tourPackageName.trim().toLowerCase()
  );
  if (!inMaster) return "ERROR";

  // --- fetch all bookings for (tourPackageName, emailAddress) ordered by tourDate ---
  // NOTE: Firestore: if 'tourDate' is a Timestamp field, orderBy works directly.
  // If it's a string date, ensure consistent comparable format (ISO).
  const sameTourSameEmail = await firebaseUtils.getCollectionData("bookings", [
    where("tourPackageName", "==", tourPackageName),
    where("emailAddress", "==", emailAddress),
    orderBy("tourDate", "asc"),
  ]);

  // --- count bookings with tourDate <= currentTourDate ---
  const count = sameTourSameEmail.filter((b: any) => {
    const d = toDate(b?.tourDate);
    return d && d.getTime() <= currentTourDate.getTime();
  }).length;

  return String(count).padStart(3, "0");
}

/**
 * Excel equivalent:
 * =UPPER(LEFT(H,1) & LEFT(I,1))
 *
 * @param firstName Traveller's first name (H column)
 * @param lastName  Traveller's last name (I column)
 * @returns Uppercased initials (2 letters) or "" if missing
 */
export default function travellerInitials(
  firstName: string | null | undefined,
  lastName: string | null | undefined
): string {
  const f = firstName && firstName.length > 0 ? firstName[0] : "";
  const l = lastName && lastName.length > 0 ? lastName[0] : "";

  const initials = (f + l).toUpperCase();

  return initials;
}
